function initialize2(x) {
  console.log("in initialize2 for "+x.toString())
  buffersWaiting[x] = 1
  console.log(clientNames[x] + " waiting stage set to 1")
  media[x] = new MediaSource()
  media[x].onsourceopen = createOpen(x)
  images[x].removeAttribute("src")
  images[x].src = window.URL.createObjectURL(media[x])
  images[x].onerror = createVideoError(x)
  images[x].autoplay = "autoplay"
  //images[x].play()
