var srcObject


function snap() {
  console.log("initializing media recorder")
  navigator.mediaDevices.getUserMedia({
      audio: {
        channelCount: 1,
        echoCancellation: false,
        noiseSuppression: false
      },
      video: {
        width: vWidth,
        height: vHeight
      }
    })
    .then(function(stream) {
      if(shortcut) {
        srcObject = stream
      }
      mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'video/webm;codecs="opus,vp8"',
        audioBitsPerSecond: audioBitsPerSecond
      })
      if(!transcodeBeforeUpload) {
        mediaRecorder.ondataavailable = function(e) {
          if(e.data && e.data.size > 0) {
            bufferIndex = (bufferIndex + 1) % bufferLength
            if(quickStop && bufferIndex == bufferLength-1) {
              quickStop = false
            }
            r[bufferIndex] = e.data
            if(quickStop && bufferIndex == bufferLength - 2 && mediaRecorder.state == "recording") {
              setTimeout("mediaRecorder.stop()", bufferDuration)
              mediaRecorder.onstop = function() {
                 quickStop=false
                snap5()
              }
            }
            upload(bufferIndex)
          }
        }
      } else {
        mediaRecorder.ondataavailable = function(e) {
          if(e.data && e.data.size > 0) {
            r.push(e.data);
          }
        }
      }
      snap5()
    }).catch(function(e) {
      handleError(e)
    })
}


function handleError(e) {
  console.log(e);
  document.getElementById("message").innerHTML = "<br><br><br><div style=\"color:red;font-size:16px\">Your camera is not connected.<br><br>Connect a camera and reload the page.</div>"
}


function firstUpdateIndices(i) {
  var x = new XMLHttpRequest()
  x.onreadystatechange = function() {
    if(x.readyState == 4 && x.status == 200) {
      indexString = x.responseText
      indices = indexString.split(",")
      if(indices.length == 0 && indices[0] == "") indices = []
      if(detailedLogging) console.log("got index of " + x.responseText + " starting " + i.toString())
      acquire(i)
      setTimeout(updateIndices, uploadPollingInterval)
    }
  }
  x.open("GET", "indices.php")
  x.send()
}

var indices


function updateIndices() {
  if(detailedLogging) console.log("entered UpdateIndices")
  var x = new XMLHttpRequest()
  x.onreadystatechange = function() {
    if(x.readyState == 4 && x.status == 200) {
      indexString = x.responseText
      indices = indexString.split(",")
      if(indices.length == 0 && indices[0] == "") indices = []
      if(detailedLogging) console.log("got index of " + x.responseText)
      setTimeout(updateIndices, uploadPollingInterval)
    }
  }
  x.open("GET", "indices.php")
  x.send()
}

var checkOnline = []
for(var iv = 0; iv < 100; iv++) {
  checkOnline.push(0)
}

var ending = []








function acquire(i) {

if(typeof(indices[i])=="undefined"){setTimeout("acquire("+i.toString()+")",acquireWait);return}


var bw=buffersWaiting[i]
var bn=bufnames[i]
var ind=parseInt(indices[i].replace(/buf/,"").replace(/end/,""))
var e=ending[i]


if(resetInactiveChannels&&parseInt(bufnames[i])!=-1&&(bufferLength+parseInt(indices[i].replace(/buf/,"").replace(/end/,""))-parseInt(bufnames[i]))%bufferLength>=bufferLength-2)
{console.log("resetting inactive channel "+i.toString());closeChannel(i)}



if(ending[i]&&detailedLogging)console.log("i: "+i+" buffersWaiting: "+buffersWaiting[i]+" bufnames: "+bufnames[i]+" index: "+indices[i])

  if(detailedLogging) console.log("entered acquire for " + i + " and bufnames[i] is " + bufnames[i])
 if(indices[i].match(/end/)) { ending[i] = true}
 if(!indices[i].match(/end/)&&(1+parseInt(indices[i].replace(/buf/,"")))%bufferLength>1)ending[i]=false //this way to fix slight complication in that last buffername 
                                                                                                        //didn't get changed to 'end' in php
  checkOnline[i] = 0
  if(i > numberOfClients) {
    console.log("value " + i.toString() + " too large, exiting") // not used
    console.log("tried to acquire from nonexistent client " + i.toString())
    bufnames[i] = 0
    return
  }

if(bw==0&&bn==-1&&(e||ind==-1)
||bw==2&&e&&bn==bufferLength-1)initialize2(i)
if(bw==1&&e&&bn==-1
||bw==0&&ind!=-1
||bw==0&&ind==-1&&bn==-1
||bw==1&&ind==-1
||bn==ind){setTimeout("acquire("+i.toString()+")",acquireWait);return}



  if(!shortcut && (!buffers[i] || buffers[i].updating || media[i].readyState != "open")) {
    if(detailedLogging) console.log("data not ready from client " + i.toString());
    if(detailedLogging) console.log("buffer " + i.toString() + ": " + buffers[i])
    if(detailedLogging) {
      if(buffers[i]) console.log("updating: " + buffers[i].updating)
    }
    if(detailedLogging) console.log("readyState: " + media[i].readyState)
    setTimeout("acquire(" + i.toString() + ")", acquireWait)
    return
  }

  buffersWaiting[i] = 2
  if(detailedLogging) console.log(clientNames[i] + " waiting stage advanced from 1 to 2")
  switchBuffers(i)()
  if(shortcut && i == nameIndexVar) return
  if(detailedLogging)console.log("requesting data from buffer " + bufnames[i].toString() + " of client " + clientNames[i])
  if(i != nameIndexVar || !getSelfFromLocal) {
    fetchSegment("image_buffer_buf" + bufnames[i].toString() + "_client_" + i.toString() + ".webm?" + Math.random().toString())
      .then(function(x) { 
        if(detailedLogging)console.log("i is "+i+" ending is " +ending+" bufnames is "+bufnames[i]+"  appending: ")
        if(detailedLogging)console.log(x)
        try {
          buffers[i].appendBuffer(x)
        } catch (e) {
          console.log("Buffer error from " + clientNames[i] + ": " + e.name);
        }
      })
  } else {
    buffers[i].appendBuffer(arrayBuffers[bufnames[i]])
  }
}



function fetchSegment(url) {
  return fetch(url).then(function(response) {
    return response.arrayBuffer();
  })
}



function switchBuffers(x) { 
  return function() {
    bufnames[x] = (bufnames[x] + 1) % bufferLength
  }
}

var restartRequestTime = -Infinity
var waitTime = -Infinity
var alreadyGotResponseTime = false

function resetShortcut() {
  if(!shortcut) return
  for(var i = 0; i < 100; i++) {
    images[i].srcObject = null
  }
  if(!hideYourself) images[nameIndexVar].srcObject = srcObject
}
var oldNameIndex = -1



function loop() {
  if(oldNameIndex != -1 && oldNameIndex != nameIndexVar) {
    resetShortcut()
  }
  oldNameIndex = nameIndexVar
  var seeking = []
  for(var ii = 0; ii < clientNames.length; ii++) {
    if(online[ii] && buffersWaiting[ii] != 2) seeking.push(clientNames[ii])
  }
  var ss = "";
  if(seeking.length > 1) ss = "s"
  if(!document.getElementById("message").innerHTML.match("not connected")) {
    if(seeking.length > 0) {
      var message = "<div style=\"color:red\">...seeking entry point for video stream" + ss + " from:"
      for(var ii = 0; ii < seeking.length; ii++) {
        message += "&nbsp;" + seeking[ii]
         if(typeof(indices[ii])!="undefined") message += "(" + indices[ii].replace(/buf/, "").replace(/end/,"") + "/" + bufferLength.toString() + ")"
      }
      message += ".</div>"
      document.getElementById("message").innerHTML = message
    } else {
      document.getElementById("message").innerHTML = defaultMessage
    }
  }
  var minus = -1
  if(!hideYourself) minus = 0
  for(var i = 0; i < 100; i++) {
    if(i >= numberOfClients) {
      clientDivs[i].style.display = "none";
      continue
    }
    var numberOfOnlineClients = 0
    for(var j = 0; j < numberOfClients; j++) {
      if(online[j] || buffersWaiting[j] > 0) numberOfOnlineClients++
      if(online[j] && (j != nameIndexVar || !shortcut)) checkOnline[j]++
      if(restartFailedReceiveLoop && checkOnline[j] > (bufferDuration * bufferLength) / 200) {
        console.log("restarting failed receive loop " + j.toString())
        closeChannel(j);
        restore(j);
        acquire(j);
      } // acquire has stopped for 1 second
    }


    if(detailedLogging) console.log("styling video " + i)
    var ww1 = Math.floor(Math.pow(hh * ww / (numberOfOnlineClients + minus + .1) * .4, 1 / 2)) * Math.pow(images[i].videoWidth / images[i].videoHeight, 1 / 2)
    var hh1 = Math.floor(Math.pow(hh * ww / (numberOfOnlineClients + minus + .1) * .4, 1 / 2)) * Math.pow(images[i].videoHeight / images[i].videoWidth, 1 / 2)
    var ww2 = Math.floor(Math.pow(hh * ww / (numberOfOnlineClients + minus + .1) * .4, 1 / 2)) * Math.pow(waitingImageAspectRatio,1/2)
    var hh2 = Math.floor(Math.pow(hh * ww / (numberOfOnlineClients + minus + .1) * .4, 1 / 2)) * Math.pow(waitingImageAspectRatio,-1/2)
    if(buffersWaiting[i] > 0) clientDivs[i].style.width = ww1.toString() + "px"
    if(online[i] && buffersWaiting[i] == 0) clientDivs[i].style.width = ww2.toString() + "px"
    if(buffersWaiting[i] > 0) clientDivs[i].style.height = (hh1 + 14).toString() + "px"
    if(online[i] && buffersWaiting[i] == 0) clientDivs[i].style.height = (hh2 + 14).toString() + "px"
    if(online[i] && buffersWaiting[i] == 0) images[i].style.height = hh2.toString() + "px"
    if(online[i] && buffersWaiting[i] == 0) images[i].style.width = ww2.toString() + "px"
    if(online[i] && buffersWaiting[i] != 2 ) tps[i].style.width = ww2.toString() + "px"
    if(buffersWaiting[i] > 0) images[i].style.width = ww1.toString() + "px"
    if(buffersWaiting[i] > 0) images[i].style.height = "" // would be hh1 anyway but better to let it retain aspect ratio
    clientDivs[i].style.display = "inline-block"
    if(!online[i] && buffersWaiting[i] == 0) clientDivs[i].style.display = "none"
    clientSpans[i].innerHTML = clientNames[i]
    if(hideYourself) clientDivs[nameIndexVar].style.display = "none"
    if(online[i] && buffersWaiting[i] != 2) {
      tps[i].style.display = "block"
    } else {
      tps[i].style.display = "none"
    }
    if(i == nameIndexVar && shortcut) tps[i].style.visibility = "hidden"
  }
  resize()
  setTimeout(loop, renderingInterval)
}



function handleNumberOfClientsChanged(a, b) {
  console.log("old number of clients: " + a.toString() + " new number: " + b.toString())
  if(a == b) return
  if(b > a) {
    for(var i = a; i <= b; i++) {
      initialize(i)
      online[i] = true
    };
    return
  }
  if(a > b) {
    window.location.href = window.location.href
  }
}

var clr

function snap5() {
  console.log("Starting media recorder")
  if(shortcut) {
    images[nameIndexVar].srcObject = srcObject
  }
  r = []
  if(!transcodeBeforeUpload) {
    mediaRecorder.start(bufferDuration)
  } else {
    mediaRecorder.start(10)
    mediaRecorder.onstart = snap8
  }
}


function snap8() {
  clr = setInterval(setupBufferTimes, bufferDuration * bufferLength)
  setupBufferTimes()
}


function setupBufferTimes() {
  for(var i = 1; i <= bufferLength; i++) {
    setTimeout("snap3(" + i.toString() + ")", bufferDuration * i)
  }
}


function snap3(ii) {
  if(detailedLogging) console.log("doing snap3")
  bufferIndex = ii - 1
  bufferIndex = bufferIndex % bufferLength
  if(quickStop && bufferIndex == bufferLength - 2 && mediaRecorder.state == "recording") {
    setTimeout("mediaRecorder.stop()", bufferDuration)
    mediaRecorder.onstop = function() {
      upload(bufferLength-1)
      quickStop = false
      snap5()
    }
  }
  upload(bufferIndex)
}


function stopOut() {
 if(bufferIndex == 0 || bufferIndex > bufferLength * 2 / 3) { // investigate why this was necessary
   setTimeout(stopOut2, bufferLength * bufferDuration * 1 / 2)
 } else {
    stopOut2()
 }
}


function stopOut2() {
  if(transcodeBeforeUpload) clearInterval(clr)
  quickStop = true;
  console.log("stopOut")
}


function nameIndex() {
  for(var i = 0; i < clientNames.length; i++) {
    if(name == clientNames[i]) {
      return i
    }
  }
}

var numberOfClients
var clientNames
var nameIndexVar


function getClientNames() {
  document.getElementById("message").innerHTML = defaultMessage
  var x = new XMLHttpRequest();
  x.onreadystatechange = function() {
    if(x.readyState == 4 && x.status == 200) {
      clientNames = x.responseText.replace(/^[^\,]*\,/, "").split(",")
      if(clientNames.length == 1 && clientNames[0] == "") clientNames = []
      nameIndexVar = nameIndex()
      numberOfClients = clientNames.length
      for(var i = 0; i < numberOfClients; i++) {
        online[i] = true
        images[i].srcObject = null
      }
      loop()
      for(var i = 0; i < numberOfClients; i++) {
        setTimeout("initialize(" + i.toString() + ")", 0)
      }
      initializeOutputIndex()
      console.log("client names: " + JSON.stringify(clientNames))
      setInterval(findNewUsers, 2000)
      if(useLatency) setInterval(stopOut, latencyInterval)
    }
  }
  x.open("GET", "ips.php")
  x.send()
}

var savedBUF = []
for(var i = 0; i < 100; i++) {
  savedBUF[i] = "none"
}

var blobs = []

function upload(buf) {
  if(detailedLogging && transcodeBeforeUpload) console.log("uploading " + r.length + " blocks in buffer " + buf.toString())
  if(detailedLogging && !transcodeBeforeUpload) console.log("uploading next block in buffer " + buf.toString())
  if(detailedLogging) console.log("from blob: ")
  var rNew = r.splice(0) // only has one defined entry in nontranscoding case
  r = [] // (not necessary but can do it anyway)
  if(transcodeBeforeUpload) {
    blobs[buf] = new Blob(rNew, {
      type: 'video/webm,codecs="opus,vp8"'
    })
  }
  var x = new XMLHttpRequest()
  x.open("POST", "webChat.php?index=" + nameIndexVar + "&buffer=buf" + buf.toString() + "&waitForThisBuffer=" + savedBUF[nameIndexVar]+"&quickStop="+quickStop.toString(), true)
  if(!transcodeBeforeUpload) {
    x.send(rNew[buf])
    if(getSelfFromLocal) fakeServerPost(buf, savedBUF[nameIndexVar], rNew[buf])
    if(detailedLogging) console.log(rNew[buf])
  } else {
    x.send(blobs[buf])
    if(getSelfFromLocal) fakeServerPost(buf, savedBUF[nameIndexVar], blobs[buf])
    if(detailedLogging) console.log(blobs[buf])
  }
  savedBUF[nameIndexVar] = "buf" + buf.toString()
}
var arrayBuffers = []


function fakeServerPost(bufNumber, wait, blob) { // no need to use wait since direct fileReader unlikely to permute blob order
blob.arrayBuffer().then(function(e){arrayBuffers[bufNumber]=e})
}



function cleanup() {
  var x = new XMLHttpRequest()
  x.open('GET', 'cleanup.php', true)
  x.send(null)
}



